int GetMyAngle(int x, int y)
{
    int ebx = -x;
    int esi = y << 11;
    int ecx = y;
    int edx = y;

    if (ebx >= 0)
    {
        // left path
        edx = ebx << 11;

        if (y >= 0)
        {
            if (ebx == y) {
                return 768;
            }
            else // loc_2F318:
            {
                if (y > ebx)
                {
                    return (AngTable[(edx / y) & kAngleMask] + 512) & kAngleMask;
                }
                else
                {
                    // loc_2F33C:
                    return ((512 - AngTable[(esi / ebx) & kAngleMask]) + 512) & kAngleMask;
                }
            }
        }
        else
        {
            // loc_2F35D:
            ecx = -y;

            if (ebx == ecx) {
                return 1280;
            }
            else if (ebx <= ecx)
            {
                return ((1024 - AngTable[(edx / ecx) & kAngleMask]) + 512) & kAngleMask;
            }
            else
            {
                edx = ecx << 11;
                return (AngTable[(edx / ebx) & kAngleMask] + 1024) & kAngleMask;
            }
        }
    }
    else
    {
        if (edx >= 0)
        {
            ebx = -ebx;

            if (ebx == edx) {
                return 256;
            }
            else if (ebx > edx)
            {
                return (AngTable[(esi / ebx) & kAngleMask] + 2048) & kAngleMask;
            }
            else
            {
                edx = ebx << 11;
                return ((2048 - AngTable[(edx / ecx) & kAngleMask]) + 512) & kAngleMask;
            }
        }
        else
        {
            ebx = -ebx;
            ecx = -ecx;

            if (ebx == ecx) {
                return 1792;
            }
            else if (ebx >= ecx)
            {
                edx = ecx << 11;
                return ((1536 - AngTable[(edx / ebx) & kAngleMask]) + 512) & kAngleMask;
            }
            else
            {
                edx = ebx << 11;
                return (AngTable[(edx / ecx) & kAngleMask] + 1536) & kAngleMask;
            }
        }
    }
}

// 100% done
int AngleDiff(int a, int b)
{
    int diff = (b - a) & kAngleMask;

    if (diff > 1024) {
        diff = 2048 - diff;
    }
    return diff;
}

int AngleDelta(int a, int b, int c)
{
    int diff = b - a;

    if (diff >= 0)
    {
        if (diff > 1024) {
            diff = -(2048 - diff);
        }
    }
    else if (diff < -1024)
    {
        diff += 2048;
    }

    if (abs(diff) > c)
    {
        if (diff < 0) {
            return -diff;
        }

        diff = c;
    }
    return diff;
}
